import React, { useState } from 'react';
import { MenuBar , Thread } from './index'

type User = {
   name:   string
   id:     string 
   color:  string
};

type Props = {
   user:    User
   setUser: React.Dispatch<React.SetStateAction<User>>
};

const Chat = (props: Props) => {

   const [nowChatSpace, setNowChatSpace] = useState("general")
   const [chatSpace, setchatSpace] = useState("general")
   const [isEditing, setIsEditing] = useState(false)

   useEffect(() => {
    axios.get('http://localhost:8080/chatSpace')
    .then(res => {
       setChatSpace(res.data)
    })
  }, [chatSpace]);

   return (
     <div>
        <header>
           <MenuBar user={props.user} setUser={props.setUser} isEditing={isEditing} setIsEditing={setIsEditing}/> 
        </header>
        <main>
           <h2 className="chatSpace">チャット一覧</h2>
           <Thread user={props.user} isEditing={isEditing}/>
        </main>
    </div>
   )
};

export default Chat;
