import React, { useState, useEffect } from 'react';
import axios from "axios";

type Props = {
  setUser: React.Dispatch<React.SetStateAction<string>>
}

const baseURL = "http://localhost:3000"

const [accounts, setAccounts] = useState(null);

const Home = (props: Props) => {

  useEffect(() => {
    axios.get('${baseURL}'+"/accounts").then((response) => {
      setAccounts(response.data);
      console.log(accounts)
    });
  }, []);

  return (
    <div className="homeContents">
      <div className="title">
        <h1>Snack</h1>
      </div> 
      <h3>ユーザーを選んでください</h3>
      <div className="accounts">
        {accounts.map((account:string, index:number)=>(
          <li key={index}>
            {accountHandle(account, props.setUser)}
          </li>
        ))}
          <br />
      </div>
      <div>
        {useAddAccountHandle(accounts)}
      </div>
    </div>
  )
}

const accountHandle = (account: string, setUser: React.Dispatch<React.SetStateAction<string>>) => {
  return (
    <button onClick={()=>setUser(account)}>
      {account}でログイン
    </button>
  )
}

const useAddAccountHandle = (account: string[]) => {

  const [newAccount, setNewAccount] = useState("")

  const submitHandle = (event: React.FormEvent<HTMLFormElement>) => {
    if (newAccount !== "") {
      accounts = [...accounts, newAccount]
      setNewAccount("")
      event.preventDefault()
    }else{
      event.preventDefault()
    };
  }

  return(
    <form onSubmit={submitHandle}>
      <div>
        <input 
          className="accountEnter"
          value={newAccount}
          placeholder="アカウントを追加"
          onChange={(event)=>{setNewAccount(event.target.value)}}
        />
      </div>
      <div>
        <input
          className="post"
          type="submit"
          value="追加"
        /> 
      </div>
    </form>
 );
};

export default Home
