import React from 'react';
import { SideMenu, AccountMenu } from './index';
import '../assets/styles/styles.css';

import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';

type User = {
   name:    string
   id:      string 
   color:   string
}

type Props = {
   user:            User
   setUser:         React.Dispatch<React.SetStateAction<User>>
   nowChannel:     string
   setNowChannel:   React.Dispatch<React.SetStateAction<string>>
   isEditing:       boolean
   setIsEditing:    React.Dispatch<React.SetStateAction<boolean>>
}

const MenuBar = (props: Props) => {
   return (
      <Box className="MenuBar">
         <AppBar>
            <Toolbar>
               <SideMenu 
                  user={props.user} 
                  setUser={props.setUser}
                  nowChannel={nowChannel}
                  setNowChannel={setNowChannel}  />
               <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                  Snack
               </Typography>
               <FormControlLabel 
                  control={<Switch onClick={() => props.setIsEditing(props.isEditing ? false : true)} color="default"/>} 
                  label="編集モード"
               />
               <AccountMenu user={props.user} setUser={props.setUser}/>
            </Toolbar>
         </AppBar>
      </Box>
   );
};

export default MenuBar;
