import React from 'react'
import '../assets/styles/styles.css';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';

type User = {
  accountName: string
  accountId:   string 
}

type Props = {
  user:          User
  setUser:       React.Dispatch<React.SetStateAction<User>>
  isEditing:     boolean
  setIsEditing:  React.Dispatch<React.SetStateAction<boolean>>
}

const Menu = (props: Props) => {

  return (
    <div className="headerMenu">
      <div className="headerContents">
        <h2>Snack</h2>
        <h4>{props.user.accountName}でログインしています</h4>
        <button onClick={() => props.setUser({accountName: "", accountId: ""})}>ログアウト</button>
        <button onClick={() => props.setIsEditing(props.isEditing ? false : true)}>編集モード</button>
        <FormControlLabel control={<Switch defaultChecked />} label="編集モード" />
      </div>
    </div>
  )
}

export default Menu
