import react, { useState } from 'react';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import MenuItem from '@mui/material/MenuItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import Settings from '@mui/icons-material/Settings';
import Divider from '@mui/material/Divider';


type User = {
  name:    string
  id:      string 
  color:   string
}

type Props = {
  user:     User
}

const renameHandle = (e :React.FormEvent<HTMLFormElement>) => {
   const data = {
     content:   editedMessage,
     id:        editedMessageId,
   };
   axios.put('http://localhost:8080/message', data)
   .then(response => {
     console.log('response body:', response.data);
     setMessageIdEdited("")
     messageRefresh()
   });
   e.preventDefault()
 };

const AccountSetting = (props: Props) => {
   const [nameEdited, setNameEdited] = useState(props.user.name)
   const [open, setOpen] = useState(false);

   const handleClickOpen = () => {
      setOpen(true);
   };

   const handleClose = () => {
      setOpen(false);
   };

   return (
      <div>
         <MenuItem onClick={()=>{handleClickOpen()}}>
            <ListItemIcon>
               <Settings fontSize="small" />
            </ListItemIcon>
            Setting
         </MenuItem>
         <Dialog open={open}>
            <DialogTitle>ユーザー名変更</DialogTitle>
            <DialogContent>
               <DialogContentText>
                  特になし
               </DialogContentText>
               <TextField
                  autoFocus
                  margin="dense"
                  value={nameEdited}
                  label="ユーザー名変更"
                  fullWidth
                  variant="standard"
                  onChange={(event)=>{setNameEdited(event.target.value)}}
               />
            </DialogContent>
            <DialogActions>
               <Button onClick={handleClose}>キャンセル</Button>
               <Button onClick={handleClose}>変更</Button>
            </DialogActions>
            <Divider variant="middle" sx={{mt: 4, mb: 2}} />
            <DialogTitle>ユーザー削除</DialogTitle>
            <DialogContentText>
                 ユーザーを削除したい場合には、下のボタンを押してください
            </DialogContentText>
            <DialogActions>
               <Button onClick={handleClose}>キャンセル</Button>
            </DialogActions>
         </Dialog>
      </div>
   );
};

export default AccountSetting;
