import React, { useState, useEffect } from 'react';
import axios from 'axios';

type User = {
  accountName:  string
  accountId:    string 
}

type Props = {
  user:      User
  isEditing: boolean
}

type Message = {
  Message:    string
  MessageId:  string
  Sender:     string
  Date:       string
  IsEdited:   boolean
}

const Thread = (props: Props) => {

  const [messages, setMessages] = useState<Message[]>([]) 
  const [newMessage, setNewMessage] = useState("")
  const [editedMessage, setMessageEdited] = useState("")
  const [editedMessageId, setMessageIdEdited] = useState("") 
  const isEdited = editedMessageId !== ""
  const [accounts, setAccounts] = useState<User[]>([]);

  const messageRefresh = () => {
    axios.get('http://localhost:8080/message')
    .then(res => {
      setMessages(res.data)
    })
  }

  const fetchAccount = () => {
    axios.get('http://localhost:8080/account')
    .then(res => {
      setAccounts(res.data)
    })
  }

  useEffect(() => {
    messageRefresh()
    fetchAccount()
  }, [messages])

  const submitHandle = (e: React.FormEvent<HTMLFormElement>) => {
    if (newMessage !== "") {
      const data = {
        message:  newMessage,
        sender:   props.user.accountId
      };
      axios.post('http://localhost:8080/message', data)
      .then(response => {
        console.log('response body:', response.data);
      });
      setNewMessage("")
      e.preventDefault()
    }else{
      e.preventDefault()
    };
  }

  const sendMessage = () => {
    return (
      <div className="submit">
        <form onSubmit={submitHandle}>
          <textarea 
            className="messageEnter"
            value={newMessage}
            placeholder="メッセージを記入"
            onChange={(event)=>{setNewMessage(event.target.value)}}
          />
          <input
            className="messagePost"
            type="submit"
            value="送信"
          /> 
        </form>
      </div>
    )
  }

  const editHandle = (message: string, messageId: string) => {
    setMessageEdited(message)
    setMessageIdEdited(messageId)
  }

  const editMessage = () => {

    const changeHandle = (e :React.FormEvent<HTMLFormElement>) => {
      const data = {
        message:    editedMessage,
        messageId:  editedMessageId,
      };
      axios.put('http://localhost:8080/message', data)
      .then(response => {
        console.log('response body:', response.data);
        console.log(data)
        setMessageIdEdited("")
      });
      e.preventDefault()
    }

    return (
      <div className="submit">
        <form onSubmit={changeHandle}>
          <textarea 
            className="messageEnter"
            value={editedMessage}
            onChange={(event)=>{setMessageEdited(event.target.value)}}
          />
          <input
            className="messagePost"
            type="submit"
            value="編集完了"
          /> 
        </form>
      </div>
    )
  }

  const deleteHandle = (messageId: string) => {
    const id = {
      MessageId: messageId   
    };
    axios.delete('http://localhost:8080/message', {data: id})
      .then(res => {        
      console.log(id);   
    })
  };

  const returnSenderName = (senderId: string) :string => {
    const senderInfo = accounts.find(function(account) {return account.accountId === senderId});
    // return senderInfo?.accountName ?? "不明" 
    return senderInfo ? senderInfo.accountName : "unknown"
  }

  const editMenu = (message: Message) => {
    return (
      <div>
        <span className="messageDelete" onClick={()=>deleteHandle(message.MessageId)}>×</span>
        <span className="messageEdit" onClick={()=>editHandle(message.Message, message.MessageId)}>編集</span>
      </div>
    )
  }

  return(
    <div>
      <div className="thread">
        {messages.map((message: Message, index: number) => (
          <li key={index}>
            {`${returnSenderName(message.Sender)}：${message.Message}`}
            {message.IsEdited ? "   <編集済み>" : null}
            { (props.isEditing && props.user.accountId===message.Sender) ? editMenu(message) : null}
          </li>
        ))}
          <br />
      </div>
      { isEdited ? editMessage() : sendMessage()}
    </div>
  );
};

export default Thread
