import React, { useState, useEffect } from 'react';
import { MenuBar , Thread } from './index'
import axios from 'axios';

type User = {
   name:   string
   id:     string 
   color:  string
};

type Props = {
   user:    User
   setUser: React.Dispatch<React.SetStateAction<User>>
};

type Channel = {
  name:   string
  id:     string 
};

const Chat = (props: Props) => {

   const [nowChannel, setNowChannel] = useState<string>("00000000000000000000000001")
   const [channels, setChannels] = useState<Channel[]>([])
   const [isEditing, setIsEditing] = useState(false)

   useEffect(() => {
    axios.get('http://localhost:8080/chatSpace')
    .then(res => {
       setChannels(res.data)
    })
  }, [channels]);

   return (
     <div>
        <header>
           <MenuBar 
              user={props.user} 
              setUser={props.setUser} 
              isEditing={isEditing} 
              setIsEditing={setIsEditing}/> 
        </header>
        <main>
           <h2 className="chatSpace">チャット一覧</h2>
           <Thread user={props.user} isEditing={isEditing}/>
        </main>
    </div>
   )
};

export default Chat;
