import React, { useState, useEffect } from 'react';
import axios from 'axios';

import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import DoneIcon from '@mui/icons-material/Done';
import DeleteIcon from '@mui/icons-material/Delete';
import SendIcon from '@mui/icons-material/Send';
import AvatarIcon from './AvatarIcon';

type User = {
  name:    string
  id:      string 
  color:   string
};

type Props = {
  user:         User
  nowChannel:   string
  isEditing:    boolean
};

type Message = {
  content:   string
  id:        string
  sender:    string
  date:      string
  edited:    boolean
};

const Thread = (props: Props) => {

  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessageContent, setNewMessageContent] = useState("");
  const [editedMessage, setMessageEdited] = useState("");
  const [editedMessageId, setMessageIdEdited] = useState("");
  const [accounts, setAccounts] = useState<User[]>([]);

  const messageRefresh = () => {
    axios.get('http://localhost:8080/message', {
      params: {
        channelId: props.nowChannel
      }
    })
    .then(res => {
      setMessages(res.data)
    })
  };

  const fetchAccount = () => {
    axios.get('http://localhost:8080/account')
    .then(res => {
      setAccounts(res.data)
    })
  };

  useEffect(() => {
    messageRefresh()
    fetchAccount()
  }, [messages, props.nowChannel]);

  const submitHandle = (e: React.FormEvent<HTMLFormElement>) => {
    if (newMessageContent !== "") {
      const data = {
        content:  newMessageContent,
        sender:   props.user.id,
        channel:  props.nowChannel
      };
      axios.post('http://localhost:8080/message', data)
      .then(response => {
        console.log('response body:', response.data);
      });
      setNewMessageContent("")
      e.preventDefault()
    }else{
      e.preventDefault()
    };
  };

  const sendMessage = () => {
    return (
      <div className="submit">   
       <Box sx={{display: 'flex'}}>
         <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0, height: 80, textAlign: 'center'}} elevation={10}>
            <form onSubmit={submitHandle}>
              <TextField sx={{ mt:1.6, width:650 }} label="メッセージを入力" value={newMessageContent} onChange={(event)=>{setNewMessageContent(event.target.value)}} />
              <Button sx={{mt:2.6, ml:1}} type="submit" variant="contained" endIcon={<SendIcon />}>
                送信
              </Button>
            </form>
         </Paper>
       </Box>
      </div>
    );
  };



// -------------------------------メッセージ編集系-------------------------------
  const editHandle = (message: string, messageId: string) => {
    setMessageEdited(message)
    setMessageIdEdited(messageId)
  };

  const editMessage = () => {

    const changeHandle = (e :React.FormEvent<HTMLFormElement>) => {
      const data = {
        content:   editedMessage,
        id:        editedMessageId,
      };
      axios.put('http://localhost:8080/message', data)
      .then(response => {
        console.log('response body:', response.data);
        setMessageIdEdited("")
        messageRefresh()
      });
      e.preventDefault()
    };

    return (
      <div className="submit">   
        <Box sx={{display: 'flex'}}>
          <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0, height: 80, textAlign: 'center'}} elevation={10}>
            <form onSubmit={changeHandle}>
              <TextField sx={{ mt:1.6, width:650 }} label="メッセージを編集" value={editedMessage} onChange={(event)=>{setMessageEdited(event.target.value)}} />
              <Button sx={{mt:2.6, ml:1}} type="submit" variant="contained" endIcon={<SendIcon />}>
                修正
              </Button>
            </form>
          </Paper>
        </Box>
     </div>
    );
  };

  const deleteHandle = (messageId: string) => {
    const deleteId = {
      id: messageId   
    };
    axios.delete('http://localhost:8080/message', {data: deleteId})
    .then(response => {
      console.log('response body:', response.data);
      messageRefresh()
    })
  };

  type SenderInfo = {
    senderName:    string
    senderColor:   string
  }

  const returnSenderInfo = (senderId: string) :SenderInfo => {
    const senderInfo = accounts.find(function(account) {return account.id === senderId});
    return senderInfo ? {senderName: senderInfo.name, senderColor: senderInfo.color} : {senderName: "unknown", senderColor : ""};
  }

  const userIsSender = (senderId: string) :boolean => {
    return props.user.id === senderId
  }

  const editMenu = (message: Message) => {
    return (
      <div className="messageMenu">
          <Stack direction="row" spacing={1}>
            <Chip
              label="編集"
              onClick={()=>editHandle(message.content, message.id)}
              onDelete={()=>editHandle(message.content, message.id)}
              deleteIcon={<DoneIcon />}
              size="small"
            />
            <Chip
              label="削除"
              onClick={()=>deleteHandle(message.id)}
              onDelete={()=>deleteHandle(message.id)}
              deleteIcon={<DeleteIcon />}
              size="small"
            />
          </Stack>
      </div>
    )
  }

  return(
    <div>
      <h2 className="channel">スレッド一覧</h2>
      <div className="thread">
        {messages?.map((message: Message, index: number) => (
          <li key={index}>
            <div className="messageInfo">
              <AvatarIcon accountName={returnSenderInfo(message.sender).senderName} accountColor={returnSenderInfo(message.sender).senderColor}/>
              <a className="senderName">{returnSenderInfo(message.sender).senderName}</a>
              <a className="sendDate">{message.date}</a>
              {message.edited ? <a className="alreadyEdited">(編集済み)</a> : null}
              {(props.isEditing && props.user.id === message.sender) ? editMenu(message) : null}
            </div>
            <div className="messageContents">
              {message.content}
            </div>
          </li>
        ))}
          <br />
      </div>
      { props.isEditing ? editMessage() : sendMessage()}
    </div>
  );
};

export default Thread;
