import React, { useState, useEffect } from 'react';
import axios from 'axios';

type User = {
   name: string
   id: string 
};

type Props = {
   setUser: React.Dispatch<React.SetStateAction<User>>
};

const Home = (props: Props) => {

   const [accounts, setAccounts] = useState<User[]>([]);
   const [newAccountName, setNewAccountName] = useState("");

   useEffect(() => {
      axios.get('http://localhost:8080/account')
      .then(res => {
         setAccounts(res.data)
      })
   }, [accounts]);

   function accountHandle(user: User, setUser: React.Dispatch<React.SetStateAction<User>>) {
      return (
         <button onClick={()=>setUser(user)}>
            {user.name}でログイン
         </button>
      );
   };

   function stringToColor(string: string) {
      let hash = 0;
      let i;

      for (i = 0; i < string.length; i += 1) {
         hash = string.charCodeAt(i) + ((hash << 5) - hash);
      }

      let color = '#';

      for (i = 0; i < 3; i += 1) {
         const value = (hash >> (i * 8)) & 0xff;
         color += `00${value.toString(16)}`.slice(-2);
      }
      return color;
   } 

   function addAccountHandle() {

      function submitHandle(event: React.FormEvent<HTMLFormElement>) {
         if (newAccountName !== "") {
            // const newAccountColor = await stringToColor(newAccountName)
            const data = {
              newAccountName:   newAccountName, 
              accountColor:     "#0000FF"
            };
            axios.post('http://localhost:8080/account', data)
            console.log(newAccountColor)
            setNewAccountName("")
         } else {
            event.preventDefault()
         };
      };

      return(
         <form onSubmit={submitHandle}>
            <div>
               <input 
                  className="accountEnter"
                  value={newAccountName}
                  placeholder="アカウントを追加"
                  onChange={(event)=>{setNewAccountName(event.target.value)}}
               />
            </div>
            <div>
               <input
                  className="post"
                  type="submit"
                  value="追加"
               /> 
            </div>
         </form>
      );
   };

   return (
      <div className="homeContents">
         <div className="title">
            <h1>Snack</h1>
         </div> 
         <h3>ユーザーを選んでください</h3>
         <div className="accounts">
            {accounts.map((account: User, index:number)=>(
               <li key={index}>
                  {accountHandle(account, props.setUser)}
               </li>
            ))}
            <br />
         </div>
            <div>
               {addAccountHandle()}
            </div>
        </div>
   );
};

export default Home;
