import React, { useState } from 'react';

type Props = {
  user: string
}

const Thread = (props: Props) => {

  const [messages, setMessages] = useState(["トークを始めよう！"]) 
  const [newMessage, setNewMessage] = useState("")
  const [messageEdited, setMessageEdited] = useState("")
  const [indexEdited, setIndexEdited] = useState(-1)
  const isEdited = indexEdited !== -1

  const submitHandle = (event: React.FormEvent<HTMLFormElement>) => {
    if (newMessage !== "") {
      setMessages([...messages, `${props.user}：${newMessage}`])
      setNewMessage("")
      event.preventDefault()
    }else{
      event.preventDefault()
    };
  }

  const sendMessage = () => {
    return (
      <div className="submit">
        <form onSubmit={submitHandle}>
          <textarea 
            className="messageEnter"
            value={newMessage}
            placeholder="メッセージを記入"
            onChange={(event)=>{setNewMessage(event.target.value)}}
          />
          <input
            className="messagePost"
            type="submit"
            value="送信"
          /> 
        </form>
      </div>
    )
  }

  const editHandle = (index :number) => {
    setIndexEdited(index) 
    setMessageEdited(messages[index]) 
  }

  const editMessage = () => {
    const changeHandle = () => {
      messages[indexEdited] = messageEdited
      setMessages(messages) 
      setMessageEdited("")
      setIndexEdited(-1)
    }
    return (
      <div className="edit">
        <form onSubmit={changeHandle}>
          <textarea 
            className="editForm"
            value={messageEdited}
            onChange={(event)=>{setMessageEdited(event.target.value)}}
          />
          <input
            className="messagePost"
            type="submit"
            value="編集完了"
          /> 
        </form>
      </div>
    )
  }

  const deleteHandle = (index :number) => {
    const newMessages = [...messages].filter((message, messageIndex)=>(messageIndex !== index))
    setMessages(newMessages)
  };

  return(
    <div>
      <div className="thread">
        {messages.map((message:string, index:number)=>(
          <li key={index}>
            {message}
            <span className="messageDelete" onClick={()=>deleteHandle(index)}>×</span>
            <span className="messageEdit" onClick={()=>editHandle(index)}>編集</span>
          </li>
        ))}
          <br />
      </div>
      { isEdited ? editMessage() : sendMessage()  }
    </div>
  );
};

export default Thread
