import { useState } from 'react';
import axios from 'axios';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

type Props = {
   setNowChannel:   React.Dispatch<React.SetStateAction<string>>
   setOpen      :   React.Dispatch<React.SetStateAction<boolean>>
}

const AddChannelDialog = (props: Props) => {
   const [newChannelName, setNewChannelName] = useState("")


   const handleClickOpen = () => {
      props.setOpen(true);
   };

   const handleClose = () => {
      setOpen(false);
   };

   const renameHandle = (e :React.FormEvent<HTMLFormElement>) => {
      if (newChannelName !== "") {
         const data = {
            name:   newChannelName
         };
         axios.put('http://localhost:8080/channel', data)
         .then(response => {
            props.setNowChannel(response.data)
         });
         handleClose()
         e.preventDefault()                
      } else {
         e.preventDefault()  
      }
    };

   return (
      <div>
         <Dialog open={open}>
            <DialogTitle>チャンネル作成</DialogTitle>
            <form onSubmit={renameHandle}>
               <DialogContent>
                  <DialogContentText>
                     作成するチャンネル名は”{newChannelName}"
                  </DialogContentText>
                  <TextField
                     autoFocus
                     margin="dense"
                     value={newChannelName}
                     label="チャンネル名"
                     fullWidth
                     variant="standard"
                     onChange={(event)=>{setNewChannelName(event.target.value)}}
                  />
               </DialogContent>
               <DialogActions>
                  <Button onClick={handleClose}>キャンセル</Button>
                  {newChannelName !== "" ? <Button type="submit">追加</Button> : <Button disabled>追加</Button>} 
               </DialogActions>
            </form>
         </Dialog>
      </div>
   );
};

export default AddChannelDialog;
