import React, { useState, useEffect } from 'react';
import axios from 'axios';

type Props = {
  setUser: React.Dispatch<React.SetStateAction<string>>
}

type AccountInfromation = {
  accountName: string
  accountId: string 
}

const baseURL = "http://localhost:8080"

const Home = (props: Props) => {
  const [accounts, setAccounts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/account')
    .then(res => {
        setAccounts(res.data)
    })
  }, [])

  const accountHandle = (accountInfo: AccountInformation, setUser: React.Dispatch<React.SetStateAction<string>>) => {
    return (
      <button onClick={()=>setUser(accountInf)}>
        {accountInf.accountName}でログイン
      </button>
    )
  }

  const useAddAccountHandle = (account: string[]) => {

    const [newAccountName, setNewAccountName] = useState("")

    const submitHandle = (event: React.FormEvent<HTMLFormElement>) => {
      if (newAccountName !== "") {
        const data = {newAccountName : newAccountName};
        axios.post('http://localhost:8080/account', data)
        .then(response => {
        console.log('response body:', response.data);
    });
        setNewAccountName("")
      }else{
        event.preventDefault()
      };
    }

    return(
      <form onSubmit={submitHandle}>
        <div>
          <input 
            className="accountEnter"
            value={newAccountName}
            placeholder="アカウントを追加"
            onChange={(event)=>{setNewAccountName(event.target.value)}}
          />
        </div>
        <div>
          <input
            className="post"
            type="submit"
            value="追加"
          /> 
        </div>
      </form>
   );
  };

  return (
    <div className="homeContents">
      <div className="title">
        <h1>Snack</h1>
      </div> 
      <h3>ユーザーを選んでください</h3>
      <div className="accounts">
        {accounts.map((account: any, index:number)=>(
          <li key={index}>
            {accountHandle(account.accountName, props.setUser)}
          </li>
        ))}
          <br />
      </div>
      <div>
        {useAddAccountHandle(accounts)}
      </div>
    </div>
  )
}

export default Home
