import react, { useState } from 'react';
import axios from 'axios';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import MenuItem from '@mui/material/MenuItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import Settings from '@mui/icons-material/Settings';
import Divider from '@mui/material/Divider';


type User = {
  name:    string
  id:      string 
  color:   string
}

type Props = {
  user:     User
}

// const accountRefresh = () => {
//    axios.get('http://localhost:8080/account')
//    .then(res => {
//      setAccounts(res.data)
//    })
//  };

const AccountSetting = (props: Props) => {
   const [nameEdited, setNameEdited] = useState(props.user.name)
   const [open, setOpen] = useState(false);

   const handleClickOpen = () => {
      setOpen(true);
   };

   const handleClose = () => {
      setOpen(false);
   };

   const renameHandle = (e :React.FormEvent<HTMLFormElement>) => {
      if (nameEdited !== "") {
         const data = {
            name:   nameEdited,
            id:     props.user.id,
          };
          axios.put('http://localhost:8080/account', data)
          .then(response => {
            console.log('response body:', response.data);
          });
          handleClose()
          e.preventDefault()         
      } else {

         e.preventDefault()  
      }
    };

   return (
      <div>
         <MenuItem onClick={()=>{handleClickOpen()}}>
            <ListItemIcon>
               <Settings fontSize="small" />
            </ListItemIcon>
            Setting
         </MenuItem>
         <Dialog open={open}>
            <DialogTitle>ユーザー名変更:変更後の名前は”{nameEdited}"</DialogTitle>
            <form onSubmit={renameHandle}>
               <DialogContent>
                  <DialogContentText>
                     特になし
                  </DialogContentText>
                  <TextField
                     autoFocus
                     margin="dense"
                     value={nameEdited}
                     label="ユーザー名変更"
                     fullWidth
                     variant="standard"
                     onChange={(event)=>{setNameEdited(event.target.value)}}
                  />
               </DialogContent>
               <DialogActions>
                  <Button onClick={handleClose}>キャンセル</Button>
                  <Button type="submit">変更</Button>
               </DialogActions>
            </form>
            <Divider variant="middle" sx={{mt: 4, mb: 2}} />
            <DialogTitle>ユーザー削除</DialogTitle>
            <DialogContentText>
                 ユーザーを削除したい場合には、下のボタンを押してください
            </DialogContentText>
            <DialogActions>
               <Button onClick={handleClose}>キャンセル</Button>
            </DialogActions>
         </Dialog>
      </div>
   );
};

export default AccountSetting;
