import React from 'react'
import '../assets/styles/styles.css';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';

import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';

type User = {
  accountName: string
  accountId:   string 
}

type Props = {
  user:          User
  setUser:       React.Dispatch<React.SetStateAction<User>>
  isEditing:     boolean
  setIsEditing:  React.Dispatch<React.SetStateAction<boolean>>
}

const Menu = (props: Props) => {

  return (
    <div className="headerMenu">
      <div className="headerContents">
        <h2>Snack</h2>
        <h4>{props.user.accountName}でログインしています</h4>
        <div className="controll">
        <FormControlLabel control={<Switch onClick={() => props.setIsEditing(props.isEditing ? false : true)}/>} label="編集モード" />
        </div>
        {useSideMenu(props)}
      </div>
    </div>
  )
}

const useSideMenu = (props: Props) => {

  const [state, setState] = React.useState(false);

  const toggleDrawer =
    (open: boolean) =>
    (event: React.KeyboardEvent | React.MouseEvent) => {
      if (
        event.type === 'keydown' &&
        ((event as React.KeyboardEvent).key === 'Tab' ||
          (event as React.KeyboardEvent).key === 'Shift')
      ) {
        return;
      }

      setState(open);
    };

  const list = () => (
    <Box
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        <h4>チャンネル</h4>
        {['UTTC', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        <ListItem key={"ログアウト"} disablePadding>
          <ListItemButton onClick={() => props.setUser({accountName: "", accountId: ""})}>
            <ListItemIcon>
              <InboxIcon />
            </ListItemIcon> 
            <ListItemText primary={"ログアウト"} />
          </ListItemButton>
        </ListItem>
      </List>
    </Box>
  );

  return (
    <div>
      {(['right'] as const).map((anchor) => (
        <React.Fragment key={anchor}>
          <Button onClick={toggleDrawer(true)}>{anchor}</Button>
          <Drawer
            anchor={anchor}
            open={state}
            onClose={toggleDrawer(false)}
          >
            {list()}
          </Drawer>
        </React.Fragment>
      ))}
    </div>
  );
}

export default Menu
