import React, { useState } from 'react';
import { Menu , Thread } from "./index"

type User = {
  accountName: string
  accountId: string 
}

type Props = {
  user: User
  setUser: React.Dispatch<React.SetStateAction<User>>
}

const [isEditing, setIsEditing] = useState(false); 

const Chat = (props: Props) => {

  return (
    <div>
      <header>
        <Menu user={props.user} setUser={props.setUser} setIsEditing={setIsEditing}/> 
      </header>
      <main>
        <h2>チャット一覧</h2>
        <Thread user={props.user}/>
      </main>
      <footer>
      </footer>
    </div>
  )
}

export default Chat
