import React, { useState } from 'react';

type Props = {
  setUser: React.Dispatch<React.SetStateAction<string>>
}

const Home = (props: Props) => {
  return (
    <div>
      <header>
        <h1>Snack</h1>
      </header> 
      <h3>ユーザーを選んでください</h3>
      <div>
        {accounts.map((account:string, index:number)=>(
          <li key={index}>
            {accountHandle(account, props.setUser)}
          </li>
        ))}
          <br />
      </div>
    </div>
  )
}

const accounts = ["りんご", "ぶどう", "いちご"]

const accountHandle = (account: string, setUser: React.Dispatch<React.SetStateAction<string>>) => {
  return (
    <button onClick={()=>setUser(account)}>
      {account}でログイン
    </button>
  )
}

const addAccountHandle = (accounts: string[], event: React.FormEvent<HTMLFormElement>) => {

  const [newAccount, setNewAccount] = useState("")

  if (newAccount !== "") {
    accounts = [...accounts, newAccount]
    setNewAccount("")
    event.preventDefault()
  }else{
    event.preventDefault()
  };

  return(
    <form onSubmit={addAccountHandle}>
      <div>
        <input 
          className="accountEnter"
          value={accounts}
          placeholder="アカウントを追加"
          onChange={(event)=>{setAccount(event.target.value)}}
        />
      </div>
      <div>
        <input
          className="post"
          type="submit"
          value="post"
        /> 
      </div>
    </form>
 );
};

export default Home
