import { useState } from 'react';
import { StringToColor, AvatarIcon } from './index';
import axios from 'axios';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import LibraryAddIcon from '@mui/icons-material/LibraryAdd';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import AddIcon from '@mui/icons-material/Add';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';

type Props = {
   setUser:        React.Dispatch<React.SetStateAction<string>>
   toggleDrawer:   (open: boolean) => (event: React.KeyboardEvent | React.MouseEvent) => void
}

const RegisterAccountDialog = (props: Props) => {
   const [newAccountName, setNewAccountName] = useState("")
   const [open, setOpen] = useState(false);

   const handleClickOpen = () => {
      setOpen(true);
   };

   const handleClose = () => {
      setOpen(false);
   };

   const registerAccountHandle = (e :React.FormEvent<HTMLFormElement>) => {
      if (newAccountName !== "") {
         const data = {
            name:   newAccountName
         };
         axios.post('http://localhost:8080/account', data)
         .then(response => {
            props.setUser(response.data)
         });
         handleClose()
         props.toggleDrawer(false)
         e.preventDefault()                
      } else {
         e.preventDefault()  
      }
    };

   return (
      <div>
         <ListItem autoFocus onClick={handleClickOpen}>
            <ListItemAvatar>
               <Avatar>
                  <AddIcon />
               </Avatar>
            </ListItemAvatar>
            <ListItemText primary="アカウントを追加" />
         </ListItem>
         <Dialog open={open}>
            <DialogTitle sx={{mr: 15, ml: 15}}>チャンネル作成</DialogTitle>
            <form onSubmit={registerAccountHandle}>
               <DialogContent>
                  <DialogContentText>
                     作成するチャンネル名は”{newAccountName}"
                  </DialogContentText>
                  <TextField
                     autoFocus
                     margin="dense"
                     value={newAccountName}
                     label="チャンネル名"
                     fullWidth
                     variant="standard"
                     onChange={(event)=>{setNewAccountName(event.target.value)}}
                  />
               </DialogContent>
               <DialogActions>
                  <Button onClick={handleClose}>キャンセル</Button>
                  {newAccountName !== "" ? <Button type="submit">追加</Button> : <Button disabled>追加</Button>} 
               </DialogActions>
            </form>
         </Dialog>
      </div>
   );
};

export default RegisterAccountDialog;
