import React, { useState, useEffect } from 'react';
import axios from 'axios';

type User = {
  accountName:  string
  accountId:    string 
}
type Props = {
  user: User
}

type Message = {
  Message:    string
  MessageId:  string
  Sender:     string
  Date:       string
  IsEdited:   boolean
}

const Thread = (props: Props) => {

  const [messages, setMessages] = useState([]) 
  const [newMessage, setNewMessage] = useState("")
  // const [messageEdited, setMessageEdited] = useState("")
  // const [indexEdited, setIndexEdited] = useState(-1)
  // const isEdited = indexEdited !== -1
  const [accounts, setAccounts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/message')
    .then(res => {
        setMessages(res.data)
    })
    axios.get('http://localhost:8080/account')
    .then(res => {
      setAccounts(res.data)
    })
    }, [])

  const submitHandle = (e: React.FormEvent<HTMLFormElement>) => {
    if (newMessage !== "") {
      const data = {
        message:  newMessage,
        sender:   props.user.accountId      
      };
      axios.post('http://localhost:8080/message', data)
      .then(response => {
      console.log('response body:', response.data);
      });
      setNewMessage("")
      e.preventDefault()
    }else{
      e.preventDefault()
    };
  }

  const sendMessage = () => {
    return (
      <div className="submit">
        <form onSubmit={submitHandle}>
          <textarea 
            className="messageEnter"
            value={newMessage}
            placeholder="メッセージを記入"
            onChange={(event)=>{setNewMessage(event.target.value)}}
          />
          <input
            className="messagePost"
            type="submit"
            value="送信"
          /> 
        </form>
      </div>
    )
  }

  // const editHandle = (index :number) => {
  //   setIndexEdited(index) 
  //   setMessageEdited(messages[index])
  // }

  // const editMessage = () => {
  //   const changeHandle = (e :React.FormEvent<HTMLFormElement>) => {
  //     messages[indexEdited] = messageEdited
  //     setMessages(messages) 
  //     setMessageEdited("")
  //     setIndexEdited(-1)
  //     e.preventDefault()

  //   }
  //   return (
  //     <div className="submit">
  //       <form onSubmit={changeHandle}>
  //         <textarea 
  //           className="messageEnter"
  //           value={messageEdited}
  //           onChange={(event)=>{setMessageEdited(event.target.value)}}
  //         />
  //         <input
  //           className="messagePost"
  //           type="submit"
  //           value="編集完了"
  //         /> 
  //       </form>
  //     </div>
  //   )
  // }

  const deleteHandle = (messageId :string) => {
    const id = {
      MessageId: messageId   
    };
    axios.delete('http://localhost:8080/message', {data: id})
      .then(res => {        
      console.log(id);     
    })
  };

  const returnSenderName = (senderId: string) => {
    const senderInfo: User[] = accounts.filter(function(account: User) {return account.accountId == senderId});
    return senderInfo[0].accountName
  }

  return(
    <div>
      <div className="thread">
        {messages.map((message: Message, index: number)=>(
          <li key={index}>
            {`${returnSenderName(message.Sender)}：${message.Message}`}
            <span className="messageDelete" onClick={()=>deleteHandle(message.MessageId)}>×</span>
            {/* <span className="messageEdit" onClick={()=>editHandle(index)}>編集</span> */}
          </li>
        ))}
          <br />
      </div>
      {/* { isEdited ? editMessage() : sendMessage() } */}
      {sendMessage()}
    </div>
  );
};

export default Thread
